import React, { useEffect, useState } from "react";
import {
  View,
  StyleSheet,
  TouchableOpacity,
  Text,
  Keyboard,
  Platform,
  BackHandler,
  Dimensions,
  Animated,
  Pressable,
} from "react-native";
import { Ionicons, Entypo } from "@expo/vector-icons";
import { Color } from "./../assets/colors";
import { Badge } from "react-native-paper";
//import Animated, { FadeIn } from "react-native-reanimated";
import { SvgXml } from "react-native-svg";
import { useSelector } from "react-redux";
import { checkVendor } from "./../Class/auth";
import { useDispatch } from "react-redux";
import { useRoute } from "@react-navigation/native";
import {
  SafeAreaView,
  useSafeAreaInsets,
} from "react-native-safe-area-context";
const { width, height } = Dimensions.get("window");

const BottomBar = (props) => {
  const navigation = props.navigation;
  const [route, setRoute] = React.useState(props.state.index);
  const [keyboardStatus, setKeyboardStatus] = React.useState(false);
  const vendorInfo = useSelector((state) => state.vendorInfo);
  const vendor = useSelector((state) => state.vendor);
  const [User, setUser] = React.useState(false);
  const user = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const isDark = useSelector((state) => state.isDark);
  const colors = new Color(isDark);
  const primaryColor = colors.getPrimaryColor();
  const textColor = colors.getTextColor();
  const assentColor = colors.getAssentColor();
  const backgroundColor = colors.getBackgroundColor();
  const hideBottomBar = useSelector((state) => state.hideBottomBar);
  const unReadNotification = useSelector((state) => state.unReadNotification);
  //const routes = useRoute();
  const [translateValue] = useState(new Animated.Value(0));

  React.useEffect(() => {
    if (vendor) {
      setUser(true);
    } else {
      setUser(false);
    }
    //console.log(vendorInfo)
  }, [vendor]);
  React.useEffect(() => {
    BackHandler.addEventListener("hardwareBackPress", () => {
      //setRoute(props.state.index)
      //console.log("red")
      return false;
    });
  }, []);
  React.useEffect(() => {
    //console.log(props.state.index);

    setRoute(props.state.index);
  }, [props?.state?.index]);
  useEffect(() => {
    press(route);
  }, [route]);

  React.useEffect(() => {
    const showSubscription = Keyboard.addListener("keyboardDidShow", () => {
      setKeyboardStatus(true);
    });
    const hideSubscription = Keyboard.addListener("keyboardDidHide", () => {
      setKeyboardStatus(false);
    });

    return () => {
      showSubscription.remove();
      hideSubscription.remove();
    };
  }, []);
  const insets = useSafeAreaInsets();

  if (keyboardStatus) {
    return <></>;
  }
  const styles = StyleSheet.create({
    box: {
      backgroundColor: primaryColor,
      flexDirection: "row",
      justifyContent: "space-between",
      paddingHorizontal: 0,
      paddingVertical: 5,
      borderTopWidth: 1,
      borderTopColor: "#F1EFEF",
    },
    button: {
      justifyContent: "center",
      alignItems: "center",
      width: width / 5,
    },
    text: {
      fontSize: 10,
      color: "#000000",
      fontWeight: "400",
      marginBottom: 2,
    },
    active: {
      color: "#4ADE80",
      fontWeight: "500",
    },
  });
  const press = (v) => {
    Animated.spring(translateValue, {
      toValue: v * (width / 5),
      velocity: 10,
      useNativeDriver: true,
    }).start();
  };

  if (hideBottomBar) {
    return null;
  }
  return (
    <Animated.View style={[styles.box, { paddingBottom: insets?.bottom }]}>
      <View style={[StyleSheet.absoluteFillObject]}>
        <Animated.View
          style={{
            width: width / 5,
            transform: [{ translateX: translateValue }],
          }}>
          <Animated.View
            style={[
              {
                width: width / 5 - 40,
                height: 2,
                marginHorizontal: 20,
                backgroundColor: "#4ADE80",
              },
            ]}
          />
        </Animated.View>
      </View>
      <Pressable
        onPress={() => {
          if (route == 0 && vendor) {
            try {
              navigation.navigate("VendorOrder");
              return;
            } catch (e) {
              console.warn(e.message);
            }
          }
          if (route == 0) {
            navigation.navigate("Feed");
            setRoute(0);
          } else {
            navigation.navigate("Home");
            setRoute(0);
          }
          press(0);
        }}
        style={styles.button}>
        {vendor ? (
          <>
            {route == 0 ? (
              <SvgXml xml={activeOrder} height="20" width="18" />
            ) : (
              <SvgXml xml={order} height="20" width="18" />
            )}
            <Text style={[styles.text, route == 0 ? styles.active : null]}>
              Order
            </Text>
          </>
        ) : (
          <>
            {route == 0 ? (
              <SvgXml xml={activeHome} height="24" width="24" />
            ) : (
              <SvgXml xml={home} height="24" width="24" />
            )}
            <Text style={[styles.text, route == 0 ? styles.active : null]}>
              Home
            </Text>
          </>
        )}
      </Pressable>
      <Pressable
        onPress={() => {
          press(1);
          if (route == 1 && vendor) {
            try {
              navigation.navigate("Search");
              return;
            } catch (e) {
              console.warn(e.message);
            }
          }
          dispatch({ type: "SET_INTEREST_CATEGORY", playload: "search" });
          if (route == 1) {
            navigation.navigate("SearchInitial");
            return;
          }
          navigation.navigate("Search");

          setRoute(1);
        }}
        style={styles.button}>
        {vendor ? (
          <>
            {route == 1 ? (
              <SvgXml xml={activeAppointment} height="20" width="20" />
            ) : (
              <SvgXml xml={appointment} height="20" width="20" />
            )}
            <Text style={[styles.text, route == 1 ? styles.active : null]}>
              Appointment
            </Text>
          </>
        ) : (
          <>
            {route == 1 ? (
              <SvgXml xml={activeSearch} height="20" width="20" />
            ) : (
              <SvgXml xml={search} height="20" width="20" />
            )}
            <Text style={[styles.text, route == 1 ? styles.active : null]}>
              Search
            </Text>
          </>
        )}
      </Pressable>
      <Pressable
        onPress={() => {
          press(2);
          dispatch({ type: "SET_INTEREST_CATEGORY", playload: "Message" });
          setRoute(2);
          if (Array.isArray(user)) {
            navigation.navigate("LogIn");
            return;
          }
          if (route == 2) {
            navigation.navigate("MessageScreen");
            return;
          }
          navigation.navigate("Message");
        }}
        style={styles.button}>
        {route == 2 ? (
          <SvgXml xml={activeMessage} height="20" width="20" />
        ) : (
          <SvgXml xml={message} height="20" width="20" />
        )}
        <Text style={[styles.text, route == 2 ? styles.active : null]}>
          Message
        </Text>
      </Pressable>
      <Pressable
        onPress={() => {
          press(3);
          dispatch({
            type: "SET_INTEREST_CATEGORY",
            playload: "Notification",
          });
          if (Array.isArray(user)) {
            navigation.navigate("LogIn");
            setRoute(3);
            return;
          }
          if (route == 3) {
            navigation.navigate("NotificationHome");
            return;
          }
          navigation.navigate("Notification");
          setRoute(3);
        }}
        style={styles.button}>
        {unReadNotification > 0 && (
          <Badge
            style={{
              position: "absolute",
              top: -5,
              left: 25,
              zIndex: 100,
            }}>
            {unReadNotification}
          </Badge>
        )}
        {route == 3 ? (
          <SvgXml xml={activeNotification} height="20" width="20" />
        ) : (
          <SvgXml xml={notification} height="20" width="20" />
        )}
        <Text style={[styles.text, route == 3 ? styles.active : null]}>
          Notification
        </Text>
      </Pressable>
      <Pressable
        onPress={() => {
          press(4);
          dispatch({
            type: "SET_INTEREST_CATEGORY",
            playload: "MainProfile",
          });
          // if (Array.isArray(user)) {
          //   setRoute(4);
          //   navigation.navigate("LogIn");
          //   return;
          // }
          if (route === 4) {
            try {
              navigation.navigate("MainProfile");
            } catch (e) {
              navigation.navigate("Profile");
              console.warn(e.message);
            }
            setRoute(4);
          } else {
            navigation.navigate("Profile");
            setRoute(4);
          }
        }}
        style={styles.button}>
        {vendor ? (
          <>
            {route == 4 ? (
              <SvgXml xml={activeProfile} height="20" width="20" />
            ) : (
              <SvgXml xml={profile} height="20" width="20" />
            )}
            <Text style={[styles.text, route == 4 ? styles.active : null]}>
              Profile
            </Text>
          </>
        ) : (
          <>
            {route == 4 ? (
              <SvgXml xml={activeProfile} height="20" width="20" />
            ) : (
              <SvgXml xml={profile} height="20" width="20" />
            )}
            <Text style={[styles.text, route == 4 ? styles.active : null]}>
              Profile
            </Text>
          </>
        )}
      </Pressable>
    </Animated.View>
  );
};

export default BottomBar;
